function getEvent(a){var b=(new Date).toLocaleDateString();a=a.toLocaleDateString();if(b==a){b=constants.calendar_events.indexOf(a);if(-1<b)return constants.calendar_event_names[b];if(-1<constants.holidays.indexOf(a))return"No school"}else{if(-1<constants.latearrival.indexOf(a))return"Late Arrival";if(-1<constants.activityperiod.indexOf(a))return"Activity Period";if(-1<constants.pmassembly.indexOf(a))return"PM Assembly";if(-1<constants.finals.indexOf(a))return"Finals"}return null}
function getNextEvents(){for(var a,b=[],c=new Date,d=0;3>d;d++){for(a=getEvent(c);null==a;)c.setDate(c.getDate()+1),a=getEvent(c);b.push(a+" - "+c.toLocaleDateString("en-US",{weekday:"long",month:"long",day:"numeric"}));c.setDate(c.getDate()+1)}return b};