$(function()
{	
	colors = ["#130912","#3E1C33","#602749","#B14623","#F6921D"];
	shuffle(colors);

	$("#tiles").children().each(function(index)
	{
		$(this).css("backgroundColor", colors[index])
	});
});

function shuffle(a, b, c, d)
{	
	c = a.length; while (c) b = Math.random() * c-- | 0, d = a[c], a[c] = a[b], a[b] = d
}
