function getLunch(c){var d=new Date(c||Date.now()),e=constants.daysInMonth.join(".").substring(0,3*d.getMonth()-1).split(".").reduce((f,g)=>{return parseInt(f)+parseInt(g)},d.getDate());return constants.lunches[e%28+""]}function getLunchString(c){var d=getLunch(c),e="";for(var f of d)e+=f+"\n";return e}